import { doSnodeBatchRequest } from '../batch-request';
export async function getSwarms({ snode, pubkey }) {
    const result = await doSnodeBatchRequest.call(this, [{
            method: 'get_swarm',
            params: {
                pubkey,
            },
        }], snode, 10000);
    const swarms = result[0].body.snodes;
    return { swarms };
}
